mapper.select:
SELECT e.id_,e.PROC_INST_ID_,e.BUSINESS_KEY_,e.proc_def_id_,e.START_TIME_, NULL as END_TIME_, NULL as duration_,
       e.START_USER_ID_,'' as delete_reason_,e.TENANT_ID_,e.name_,
       'ACTIVE' as status
FROM act_ru_execution e INNER JOIN act_re_procdef p ON e.proc_def_id_ = p.id_


        e.start_user_id_ = #{startUserId}


        AND e.tenant_id_ = #{tenantId}


        AND e.name_ like concat('%',#{name},'%')


        AND e.business_key_ like concat('%',#{businessKey},'%')

  ORDER BY e.start_time_ desc limit #{page},#{size}

SELECT count(1)
FROM act_ru_execution e INNER JOIN act_re_procdef p ON e.proc_def_id_ = p.id_


        e.start_user_id_ = #{startUserId}


        AND e.tenant_id_ = #{tenantId}


        AND e.name_ like concat('%',#{name},'%')


        AND e.business_key_ like concat('%',#{businessKey},'%')

SELECT e.id_,e.PROC_INST_ID_,e.BUSINESS_KEY_,e.proc_def_id_,e.START_TIME_, e.END_TIME_, e.duration_, e.START_USER_ID_,e.delete_reason_,e.TENANT_ID_,e.name_, 'COMPLETE' as status FROM act_hi_procinst e LEFT JOIN act_hi_varinst d ON e.id_ = d.PROC_INST_ID_ AND d.NAME_ = 'PROCESS_STATUS' AND d.TEXT_ = 'CANCEL'
e.start_user_id_ = #{startUserId}
AND e.tenant_id_ = #{tenantId}
AND e.name_ like concat('%',#{name},'%')
AND e.business_key_ like concat('%',#{businessKey},'%')
AND e.end_time_ IS NOT NULL AND d.id_ IS NULL ORDER BY e.start_time_ DESC limit #{page},#{size}

SELECT count(1) FROM act_hi_procinst e LEFT JOIN act_hi_varinst d ON e.id_ = d.PROC_INST_ID_ AND d.NAME_ = 'PROCESS_STATUS' AND d.TEXT_ = 'CANCEL'
e.start_user_id_ = #{startUserId}
AND e.tenant_id_ = #{tenantId}
AND e.name_ like concat('%',#{name},'%')
AND e.business_key_ like concat('%',#{businessKey},'%')
AND e.end_time_ IS NOT NULL AND d.id_ IS NULL

SELECT e.id_,e.PROC_INST_ID_,e.BUSINESS_KEY_,e.proc_def_id_,e.START_TIME_, e.END_TIME_, e.duration_, e.START_USER_ID_,e.delete_reason_,e.TENANT_ID_,e.name_, 'CANCEL' as status FROM act_hi_procinst e LEFT JOIN act_hi_varinst d ON e.id_ = d.PROC_INST_ID_ AND d.NAME_ = 'PROCESS_STATUS' AND d.TEXT_ = 'CANCEL'
e.start_user_id_ = #{startUserId}
AND e.tenant_id_ = #{tenantId}
AND e.name_ like concat('%',#{name},'%')
AND e.business_key_ like concat('%',#{businessKey},'%')
AND e.end_time_ IS NOT NULL AND d.id_ IS NOT NULL ORDER BY e.start_time_ DESC limit #{page},#{size}

SELECT count(1) FROM act_hi_procinst e LEFT JOIN act_hi_varinst d ON e.id_ = d.PROC_INST_ID_ AND d.NAME_ = 'PROCESS_STATUS' AND d.TEXT_ = 'CANCEL'
e.start_user_id_ = #{startUserId}
AND e.tenant_id_ = #{tenantId}
AND e.name_ like concat('%',#{name},'%')
AND e.business_key_ like concat('%',#{businessKey},'%')
AND e.end_time_ IS NOT NULL AND d.id_ IS NOT NULL

SELECT e.id as id_,'' as PROC_INST_ID_, e.BUSINESS_KEY as BUSINESS_KEY_,'' as proc_def_id_,
e.created_time as START_TIME_, null as END_TIME_, '' as duration_, e.user_id as START_USER_ID_,
'' as delete_reason_,e.tenant_id as TENANT_ID_,e.model_name as name_,
'PREPARE' as status
FROM hiss_process_pre_launch e


        e.user_id = #{startUserId}


        AND e.tenant_id = #{tenantId}


        AND e.model_name like concat('%',#{name},'%')


        AND e.business_key like concat('%',#{businessKey},'%')

  ORDER BY e.created_time DESC limit #{page},#{size}

SELECT count(1)
FROM hiss_process_pre_launch e


        e.user_id = #{startUserId}


        AND e.tenant_id = #{tenantId}


        AND e.model_name like concat('%',#{name},'%')


        AND e.business_key like concat('%',#{businessKey},'%')

SELECT * FROM (SELECT e.id_,e.PROC_INST_ID_,e.BUSINESS_KEY_,e.proc_def_id_,e.START_TIME_, e.END_TIME_, e.duration_, e.START_USER_ID_,e.delete_reason_,e.TENANT_ID_,e.name_, case when d.NAME_='PROCESS_STATUS' and d.TEXT_='CANCEL' then 'CANCEL' when e.end_time_ is not NULL then 'COMPLETE' when e.end_time_ is NULL then 'ACTIVE' end as status FROM act_hi_procinst e LEFT JOIN act_hi_varinst d ON e.id_ = d.PROC_INST_ID_ AND d.NAME_ = 'PROCESS_STATUS' AND d.TEXT_ = 'CANCEL'
e.start_user_id_ = #{startUserId}
AND e.tenant_id_ = #{tenantId}
AND e.name_ like concat('%',#{name},'%')
AND e.business_key_ like concat('%',#{businessKey},'%')
UNION SELECT e.id as id_,'' as PROC_INST_ID_, e.BUSINESS_KEY as BUSINESS_KEY_,'' as proc_def_id_, e.created_time as START_TIME_, null as END_TIME_, '' as duration_, e.user_id as START_USER_ID_, '' as delete_reason_,e.tenant_id as TENANT_ID_,e.model_name as name_, 'PREPARE' as status FROM hiss_process_pre_launch e
e.user_id = #{startUserId}
AND e.tenant_id = #{tenantId}
AND e.model_name like concat('%',#{name},'%')
AND e.business_key like concat('%',#{businessKey},'%')
) m ORDER BY m.START_TIME_ DESC limit #{page},#{size}

SELECT count(1) FROM (SELECT e.id_ FROM act_hi_procinst e LEFT JOIN act_hi_varinst d ON e.id_ = d.PROC_INST_ID_ AND d.NAME_ = 'PROCESS_STATUS' AND d.TEXT_ = 'CANCEL'
e.start_user_id_ = #{startUserId}
AND e.tenant_id_ = #{tenantId}
AND e.name_ like concat('%',#{name},'%')
AND e.business_key_ like concat('%',#{businessKey},'%')
UNION SELECT e.id as id_ FROM hiss_process_pre_launch e
e.user_id = #{startUserId}
AND e.tenant_id = #{tenantId}
AND e.model_name like concat('%',#{name},'%')
AND e.business_key like concat('%',#{businessKey},'%')
) m
